public int maxSubArray(int[] nums) {
    if (nums.length == 0) {
        return 0;
    }
    int maxSum = nums[0];
    int curSum = nums[0];
    for (int i = 1; i  0) {
            curSum += nums[i];
        } else {
            curSum = nums[i];
        }
        maxSum = Math.max(maxSum, curSum);
    }
    return maxSum;
}

