public int[] sortedSquares(int[] nums) {
    if (nums == null || nums.length == 0) {
        return nums;
    }
    int header = 0;
    int tail = nums.length - 1;
    int[] res = new int[nums.length];
    int i = tail;
    while (header  nums[tail] * nums[tail]) {
            res[i] = nums[header] * nums[header];
            header ++;
        } else {
            res[i] = nums[tail] * nums[tail];
            tail --;
        }
        i --;
    }
    return res;
}

