public static ListNode swapPairs(ListNode head) {
    if (head == null || head.next == null) {
        return head;
    }
    ListNode preHead = new ListNode(0);
    preHead.next = head;
    ListNode pre = preHead;
    ListNode cur = head;
    ListNode next = null;
    int i = 0;
    while (cur.next != null) {
        i++;
        next = cur.next;
        ListNode nextNext = cur.next.next;
        if (i % 2 == 1) { //2怎么丢了？？？
            next.next = cur;
            pre.next = next;
            cur.next = nextNext;
        } else {
            pre = cur;
            cur = next;
        }
    }
    return preHead.next;
}

